sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

     // Contruct a GlideRecord query to find all birthays with a matching month
	// and day as the current occasion.

	var sameBirthday = new GlidRecord('x_snc_employee_spe_occasions');
	sameBirthday.addQuery('occasion_month', current.occasion_month);
	sameBirthday.addQuery('occasion_day', current.occasion_month);
	sameBirthday.addQuery('occasion',  'birthday');

	sameBirthday.query();

	//Print to the Message field on a notification
	template.print(sameBirthday.getRowCount() -1 + "employee in the company share your birthday.");


})(current, template, email, email_action, event);
